#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

#include "stdio.h"
#include "stdint.h"





//clock
#define RCC_BASE 	    0x40021000
#define RCC_APB2ENR     *(volatile uint32_t*)(RCC_BASE + 0x18)
#define RCC_IOPAEN 		(1<<2)

//Port A
#define PORTA_BASE 	    0x40010800
#define GPIOA_CRL		*(volatile uint32_t*)(PORTA_BASE + 0x00)
#define GPIOA_IDR		*(volatile uint32_t*)(PORTA_BASE + 0x08)
#define GPIOA_ODR		*(volatile uint32_t*)(PORTA_BASE + 0x0c)
#define GPIOA_CRH		*(volatile uint32_t*)(PORTA_BASE + 0x04)


#define PORTB_BASE 	    0x40010C00
#define GPIOB_CRL		*(volatile uint32_t*)(PORTB_BASE + 0x00)
#define GPIOB_IDR		*(volatile uint32_t*)(PORTB_BASE + 0x08)
#define GPIOB_ODR		*(volatile uint32_t*)(PORTB_BASE + 0x0c)
#define GPIOB_CRH		*(volatile uint32_t*)(PORTB_BASE + 0x04)




void clock_init(){
	//Enable clock of GPIOA
	RCC_APB2ENR |= RCC_IOPAEN;
	//Enable clock of GPIOB
	RCC_APB2ENR |= (1<<3);


}
void GPIO_init(){
	//PA1 is input floating
	GPIOA_CRL &= ~(0b11 <<4);
	GPIOA_CRL |= (1<<6);

	//PB1 is output push pull mode
	GPIOB_CRL = 0;
	GPIOB_CRL = (1<<4);

	//PA13 is input floating
	GPIOA_CRH = 0;
	GPIOA_CRH |= (1<<22);


	//PB13 is output push pull mode
	GPIOB_CRH |= (0b01<<20);
	GPIOB_CRH &= ~(0b11<<22);

}

void wait(){
	for(int i=0; i<2; i++){
		for(int j=0; j<255; j++);
	}
}
int main(void)
{
	clock_init();
	GPIO_init();

	while(1)
	{
		if((GPIOA_IDR & (1<<1))>>1 == 0){
			GPIOB_ODR ^= (1<<1);
			while((GPIOA_IDR & (1<<1))>>1 == 0);
		}
		if((GPIOA_IDR & (1<<13))>>13 == 1){
			GPIOB_ODR ^= (1<<13);
		}
		wait();
	}
}





